}

  render(){
    console.log("render")
  return (
  <div>
    <input type="text" onChange={(e) => this.showText(e.target.value)}></input>
    <p>{this.state.displayText}</p>
    <div id="chat">
      <div id="chat-window">
        <div id="output">{this.state.displayText}

        </div>
        <input id="handle" type="text" placeholder="Handle"/>
        <input id="message" type="text" placeholder="Message"/>
        <button id="send" onClick={(event) => this.emitMessage(text.target.parentElement.children[1].value, event.target.value)}>Send</button>
      </div>
    </div>
  </div>
  );
  }
}

export default App;
