//io.emit('random', "table tennis*")  test in console

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      user: null,
      userList: []
    }
  }

  login = (e) => {
    let username = e.target.parentElement.children[0].value
    let password = e.target.parentElement.children[1].value
    fetch('http://localhost:8000/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        username: username, password: password
      })
    })
    .then(res => {
      localStorage.setItem('userToken', res.data)
      return res.data.userToken
    })
    .catch(err => {
      console.log(err)
    })

  }




  createUser = (e) => {
    let username = e.target.parentElement.children[0].value
    let password = e.target.parentElement.children[1].value
    fetch('http://localhost:8000/register',{
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        username: username, password: password
      })
    })
    .then(res => res.json())
    .then(user => {
      if(user.error){
        alert(user.error);
      } else
      {let newUserList = this.state.userList
      newUserList.push(user)
      this.setState({
        user: user,
        userList: newUserList
      })}
    })
  }

  render() {
    return (
      <div>
        <Header />
        {
          this.state.user ? <Dashboard user={this.state.user} userlist={this.state.userList}/> : <Login login={this.login} createUser={this.createUser}/>
        }
      </div>
    );
  }
}

export default App;
