import React, { Component } from "react";
import './App.css';
import socketIOClient from 'socket.io-client'

const io = socketIOClient('http://10.185.2.208:8080')
window.io = io

// io.on('message', payload => {
//   console.log(payload)
// })

class App extends Component {

  constructor() {
    super()
    this.state = {
      displayText: ""
    }
  }

  showText = (text) => {
  io.emit('random', text)
  this.setState({
    displayText: text
  })
  }

  // io.on("input", {

  // })


  render(){
  return (
  <div>
    <input type="text" onChange={(e) => this.showText(e.target.value)}></input>
    <p>{this.state.displayText}</p>
    <div id="chat">
      <div id="chat-window">
        <div id="output">

        </div>
        <input id="handle" type="text" placeholder="Handle"/>
        <input id="message" type="text" placeholder="Message"/>
        <button id="send"></button>
      </div>
    </div>
  </div>
  );
  }
}

export default App;
