//io.emit('random', "table tennis*")  test in console

class App extends React.Component {
  constructor(){
    super()
    this.state = {
      user: null,
      userList: []
    }
  }

  login = (e) => {
    let username = e.target.parentElement.children[0].value
    let password = e.target.parentElement.children[1].value
    let user = {username: username}
    let newUserList = this.state.userList.push(user)
    this.setState({
      user: user,
      userList: newUserList
    })
  }

  fetchUser = (username) => {
    fetch('http://localhost:8000/users')
    .then(res => res.json())
    .then(users => {
      this.setState({
        user: users.find(user => {return user.username === username})
      })
    })
  }

  createUser = (e) => {
    let username = e.target.parentElement.children[0].value
    let password = e.target.parentElement.children[1].value
    fetch('http://localhost:8000/users',{
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        username: username, password: password
      })
    })
    .then(res => res.json())
    .then(user => {
      this.login(e)
    })
  }

  render() {
    return (
      <div>
        <Header />
        {
          this.state.user ? <Dashboard /> : <Login login={this.login} createUser={this.createUser}/>
        }
      </div>
    );
  }
}

export default App;
